// Animation for Alert

.repeal-day-ball h3 { color: #FFF }

.repeal-day-ball p { color: #FFF }

.repeal-day-ball a { color: #FFF; border-bottom: 2px solid rgba(0,0,0,.15) }

@-webkit-keyframes popIn {
  0%   { opacity: 0; -webkit-transform: scale(2, 2) }
  100% { opacity: 1; -webkit-transform: scale(1, 1) }
}

@-moz-keyframes popIn {
  0%   { opacity: 0; -moz-transform: scale(2, 2) }
  100% { opacity: 1; -moz-transform: scale(1, 1) }
}

@-ms-keyframes popIn {
  0%   { opacity: 0; -ms-transform: scale(2, 2) }
  100% { opacity: 1; -ms-transform: scale(1, 1) }
}

@keyframes popIn {
  0%   { opacity: 0; transform: scale(2, 2) }
  100% { opacity: 1; transform: scale(1, 1) }
}
